import React, { useState, useEffect } from "react";

// Load local JSON database
import sampleData from "../data/components.json";

export default function ComponentList() {
  const [components, setComponents] = useState([]);

  useEffect(() => {
    // Simulate loading from local JSON
    setComponents(sampleData);
  }, []);

  return (
    <div>
      <h2>Component Manager</h2>
      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Type</th>
            <th>Description</th>
            <th>Location</th>
            <th>Notes</th>
          </tr>
        </thead>
        <tbody>
          {components.map((c) => (
            <tr key={c.id}>
              <td>{c.id}</td>
              <td>{c.name}</td>
              <td>{c.type}</td>
              <td>{c.description}</td>
              <td>{c.location}</td>
              <td>{c.notes}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <p style={{ marginTop: 16 }}>
        (Editable fields and new component add/delete functionality coming next)
      </p>
    </div>
  );
}
